/* Global Styles */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    outline: none;
    -webkit-tap-highlight-color: transparent;
}

html {
    scroll-behavior: smooth;
}

body {
    min-width: 320px;
    font-family: "Raleway", sans-serif;
    font-family: "Roboto Mono", monospace;
    line-height: 1.5;
    transition: background-color 0.5s ease;
}

.container {
    width: 80%;
    max-width: 1280px;
    margin: auto;
    overflow: hidden;

    @media screen and (max-width: 768px) {
        width: 90%;
    }
}

ul {
    list-style: none;
}

a {
    text-decoration: none;
    color: inherit;
}

img {
    display: inline-block;
}

.no-display {
    display: none;
}

.animated {
    animation: appear 2s;
    animation-iteration-count: 1;
    animation-fill-mode: forwards;
}

@keyframes appear {
    0% {
        opacity: 0;
    }

    100% {
        opacity: 1;
    }
}

/* Dark Mode Toggle Button Styles */
.dark-mode-toggle-button {
    .dark-mode-checkbox {
        position: absolute;
        visibility: hidden;

        &:checked+.dark-mode-label {
            background-color: $white-color;
            color: $black-color;

            .dark-mode-ball {
                transform: translateX(1.5rem);
            }
        }
    }

    .dark-mode-label {
        display: flex;
        align-items: center;
        justify-content: space-between;
        width: 4rem;
        height: 2rem;
        color: $white-color;
        padding: 0.25rem 0.5rem;
        border-radius: 4rem;
        cursor: pointer;

        .fa-moon,
        .fa-sun {
            font-size: 1rem;
        }

        .dark-mode-ball {
            display: inline-block;
            position: absolute;
            width: 1.5rem;
            height: 1.5rem;
            background-color: $primary-color;
            border-radius: 50%;
            transition: transform 0.3s linear;
        }
    }
}

.type-writer {
    white-space: nowrap;
    animation:
        typing 3.5s steps(40, end)
}

@keyframes typing {
    from {
        width: 0
    }

    to {
        width: 100%
    }
}

.blinking-dark {
    animation: blink-dark 1s infinite;
}

.blinking-light {
    animation: blink-light 1s infinite;
}

@keyframes blink-light {
    0% {
        color: #000;
    }

    49% {
        color: #000;
    }

    60% {
        color: transparent;
    }

    99% {
        color: transparent;
    }

    100% {
        color: #000;
    }
}

@keyframes blink-dark {
    0% {
        color: #fff;
    }

    49% {
        color: #fff;
    }

    60% {
        color: transparent;
    }

    99% {
        color: transparent;
    }

    100% {
        color: #fff;
    }
}

.section-link,
.show-more-projects {
    text-transform: uppercase;
    background-color: inherit;
    color: inherit;
    font-family: inherit;
    font-weight: bold;
    padding: 1rem;
    border: 2px solid $grey-color;
    cursor: pointer;

    &:hover {
        border: 2px solid $primary-color;

        .fa-angle-down {
            color: $primary-color;
        }
    }
}

.section-title {
    text-transform: uppercase;
    text-align: center;
    padding: 0.25rem 0;
}

.section-sub-title {
    @extend .regular-text;
    @extend .primary-text;
    @extend .bold-text;
    @extend .raleway-font;
    text-align: center;
    padding: 1rem;
    margin-bottom: 1.5rem;
}

.underline {
    height: 3px;
    width: 3rem;
    margin: auto;
    background: $primary-color;
}

/* Typography Styles */
.extra-large-text {
    font-size: $extra-large-font;

    @media screen and (max-width: 768px) {
        font-size: $large-font;
    }
}

.large-text {
    font-size: $large-font;

    @media screen and (max-width: 768px) {
        font-size: $medium-font;
    }
}

.medium-text {
    font-size: $medium-font;

    @media screen and (max-width: 768px) {
        font-size: $regular-font;
    }
}

.regular-text {
    font-size: $regular-font;

    @media screen and (max-width: 768px) {
        font-size: $small-font;
    }
}

.small-text {
    font-size: $small-font;
}

.primary-text {
    color: $primary-color;
}

.bold-text {
    font-weight: bold;
}

.raleway-font {
